import React, {useRef, useState} from 'react';
import {View, Text, Image, StyleSheet, Dimensions, ScrollView, ImageBackground, TouchableOpacity} from 'react-native';
import {logger} from "../../static_scripts/tools/logger";

const data = [
    { id: '1', title: 'First Slide', image: 'data:image/jpeg;base64,' },
    { id: '2', title: 'Second Slide', image: 'data:image/jpeg;base64,' },
    { id: '3', title: 'Third Slide', image: 'data:image/jpeg;base64,' },
];

const Carousel = () => {
    const { width } = Dimensions.get('window');
    const [activeIndex, setActiveIndex] = useState(0);

    const scrollViewRef = useRef(null);
    logger.writeLog(`scrollViewRef --> ${scrollViewRef.current}`)

    const handleScroll = (event) => {
        const scrollPosition = event.nativeEvent.contentOffset.x;
        logger.writeLog(`scrollPosition --> ${scrollPosition}`);
        const index = Math.round(scrollPosition / width);
        logger.writeLog(`index --> ${index}`);

        setActiveIndex(index);

        if (index === data.length - 1) {
            setTimeout(() => {
                scrollViewRef.current.scrollTo({ x: 0 });
                setActiveIndex(0);
            }, 500);
        }
    };

    return (
        <View>
            <ScrollView
                ref={scrollViewRef}
                horizontal
                pagingEnabled
                onScroll={handleScroll}
                scrollEventThrottle={16}
                showsHorizontalScrollIndicator={false}
            >
                {data.map((item) => (
                    <View key={item.id} style={[styles.slide, { width }]}>
                        <ImageBackground source={{ uri: item.image }} resizeMode="cover" style={styles.image}>
                                <View style={styles.buttonContainer}>
                                    <TouchableOpacity style={styles.button}>
                                        <Text style={styles.buttonText}>За покупками</Text>
                                    </TouchableOpacity>
                                </View>
                        </ImageBackground>

                    </View>
                ))}
            </ScrollView>
            {/*<View style={styles.pagination}>*/}
            {/*    {data.map((_, index) => (*/}
            {/*        <View*/}
            {/*            key={index}*/}
            {/*            style={[styles.dot, activeIndex === index && styles.activeDot,*/}
            {/*            ]}*/}
            {/*        />*/}
            {/*    ))}*/}
            {/*</View>*/}
        </View>
    );
};

const styles = StyleSheet.create({
    slide: {
        alignItems: 'center',
    },
    image: {
        width: '90%',
        height: 200,
        borderRadius: 20,
    },
    title: {
        marginTop: 10,
        fontSize: 18,
        color: '#333',
    },
    pagination: {
        flexDirection: 'row',
        justifyContent: 'center',
        marginTop: 10,
    },
    dot: {
        width: 10,
        height: 10,
        borderRadius: 5,
        backgroundColor: '#ccc',
        marginHorizontal: 5,
    },
    activeDot: {
        backgroundColor: '#333',
    },
    buttonContainer: {
        top: 140,
        left: 20
    },
    button: {
        paddingHorizontal: 20,
        paddingVertical: 12,
        backgroundColor: '#733c3c',
        width: '40%',
        height: 40,
        borderRadius: 10,
        justifyContent: 'center',
        alignItems: 'center',
    },
    buttonText: {
        fontSize: 15,
        fontWeight: 'bold',
        color: 'white'
    },
});

export default Carousel;